<?php          /** * @category    Magebuzz * @package     Magebuzz_Multipleorderemail */class Magebuzz_Multipleorderemail_Model_Multipleorderemailrule extends Magebuzz_Multipleorderemail_Model_Abstract{    protected $_eventPrefix = 'multipleorderemail_rule';       public function _construct()    {            parent::_construct();        $this->_init('multipleorderemail/multipleorderemailrule');    }    public function getConditionsInstance()    {        return Mage::getModel('salesrule/rule_condition_combine');    }    public function getActionsInstance()    {        return Mage::getModel('multipleorderemail/rule_condition_product_combine');    }    /**     * Initialize rule model data from array     *     * @param   array $rule     * @return  Mage_SalesRule_Model_Rule     */    public function loadPost(array $rule)    {        $arr = $this->_convertFlatToRecursive($rule);        if (isset($arr['conditions'])) {            $this->getConditions()->setConditions(array())->loadArray($arr['conditions'][1]);        }        if (isset($arr['actions'])) {            $this->getActions()->setActions(array())->loadArray($arr['actions'][1], 'actions');        }        if (isset($rule['store_labels'])) {            $this->setStoreLabels($rule['store_labels']);        }        return $this;    }}